INSERT INTO `article_data` VALUES ('54', '<p>\r\n	<p style=\"font-family:sans-serif;font-size:16px;\">\r\n		<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/f6gOI6H6AO7oeHg1f19c7EO97AhaDW.gif\" width=\"100%\" alt=\"关注8.gif\" />\r\n	</p>\r\n<pre>\r\n	<h1 style=\"text-align:center;\">\r\n		\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"> </span></span>\r\n		</div>\r\n\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"font-size:18px;\"> 许多人都知道，韭菜含有丰富的营养价值，除了可壮阳固精外，还有治病的作用。在医学界，韭菜被称为“洗肠草”、它可预防习惯性</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"font-size:18px;\">便秘，还可将消化道中的某些杂物包裹起来，随大便排出体外，具保暖、健胃的功效，其所含的粗纤维可促进肠蠕动，帮助人体消化的功效。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><img width=\"auto\" src=\"http://img.8htq.com.cn/upload/20161026/image95/147745610095.jpg?wx_fmt=png&amp;tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" /></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">但韭菜的治病保健功效远远不止这些，今天，小编就为大家搜罗了韭菜的29种药用妙招及其保健功效，赶紧告诉家人吧!</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">1、治阳痿</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">对男人来说，韭菜可是天然“伟哥，”韭菜益精壮阳的两种做法：①韭菜粥：鲜韭菜50克，韭菜籽10克(研细末)，粳米100克，细盐少许。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">先煮粳米为粥，待粥快熟时加入韭菜(洗净切断)或韭菜籽末、细盐，稍煮片刻即成。功能：补肾壮阳、固精止遗、健脾暖胃。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">②韭菜炒鸡蛋：新鲜韭菜100克，洗净切碎，鸡蛋3只(去壳)，同切碎之韭菜捣匀，用素油、食盐同炒至熟佐食。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">功能：温中养血、温肾暖腰膝。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><img width=\"auto\" src=\"http://img.8htq.com.cn/upload/20161026/image67/147745610067.jpg?tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" /></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h2 style=\"font-family:sans-serif;font-size:16px;font-weight:400;\">\r\n	</h2>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">2、治白带</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">醋煮韭菜籽焙干研末，炼蜜为丸。空腹以酒送服，每次30丸(丸如梧桐子大)。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h2 style=\"font-family:sans-serif;font-size:16px;font-weight:400;\">\r\n	</h2>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">3、治血崩</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜5两，煮糯米酒服之。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h2 style=\"font-family:sans-serif;font-size:16px;font-weight:400;\">\r\n	</h2>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">4、治子宫脱垂</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜半斤。煎汤熏洗外阴部。 </span></strong><strong><span style=\"color:#0074C1;font-size:18px;\">5、治产后血晕 </span></strong><strong><span style=\"color:#595959;font-size:18px;\">韭菜(切)入瓶内，注热醋，以瓶口对鼻。 </span></strong><strong><span style=\"color:#0074C1;font-size:18px;\">6、治孕吐</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:center;\">\r\n			<strong></strong>\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜汁50毫升，生姜汁10毫升，加糖适量，凋服 </span></strong><strong><span style=\"color:#0074C1;font-size:18px;\"> 7、治鼻出血</span></strong>\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> 韭菜捣汁1杯，夏日冷服，冬天温服;阴虚血热引起鼻衄，用鲜韭菜根洗净后捣烂堵鼻孔内。 </span></strong><strong><span style=\"color:#0074C1;font-size:18px;\">8、治翻胃</span></strong>\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜汁二两，牛乳一盏。上用生姜汁半两，和匀。温服。 </span></strong><strong><span style=\"color:#0074C1;font-size:18px;\">9、治慢性便秘</span></strong>\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n	<h1 style=\"text-align:center;\">\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n<pre>\r\n<pre>\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;line-height:1.75em;font-size:18px;\">韭菜有“绿色蔬菜之王”美称，含有丰富的纤维素，每100克韭菜含1.5克纤维素，能刺激肠道，增强肠蠕动，有利于粪便的形成，</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;line-height:1.75em;font-size:18px;\">及时将体内过剩的营养物随粪便排出，有利于预防肠癌和防治习惯性便秘。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;line-height:1.75em;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"font-size:18px;color:#595959;line-height:1.75em;\">另外，这些纤维还可以把消化道中的沙砾、头发、金属屑包裹起来，随大便排出，故有“洗肠草”之称。也可用韭菜根叶捣汁1杯，温开水加少许酒冲服。 </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">10、治赤痢</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:center;\">\r\n			<strong></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜一把去梢取汁，和酒l盅，名日“韭汁酒”，温服。</span></strong> \r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">11、治痔疮</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜不以多少，先烧热汤，以盆盛汤在内，盆上用器具盖之，留一窍，却以韭菜于汤内泡之，以谷道坐窍上，令气蒸熏;候温，用韭菜轻轻洗疮数次。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">12、治脱肛不缩</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">生韭一斤。细切，以酥拌炒令熟，分为两处，以软帛裹，更互熨之，冷即再易，以入为度。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">13、治顽癣</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜焙干，研细末，猪油调和敷患处。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">14、牛皮癣</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">可用生韭、蒜各30克，捣烂如泥，烘热后用力擦患处，每日1次，连续数日。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">15、治过敏性皮炎</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜捣汁外涂，日数次。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">16、治汗斑</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜捣汁，每日晨起外擦患部2次。连用4～5日。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">17、治过敏性紫癜</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">鲜韭菜一斤，洗净，捣烂绞汁，加健康健康儿童尿50毫升。日一剂，分二次服。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">18、治荨麻疹</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜、甘草各五钱，煎服;或用韭菜炒食。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">19、治漆疮作痒</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭叶杵敷。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><img height=\"365\" width=\"auto\" src=\"http://img.8htq.com.cn/upload/20161026/image74/147745610174.jpg?tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" /></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">20、治盗汗、自汗</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭根适量煎汁内服，每日2次。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">21、跌打损伤</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜或根，捣烂敷外伤处能消肿疲血肿痛止痛、止血。或鲜韭菜三份，面粉一份。共捣成糊状。敷于患处，每日二次。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">22、治扭伤腰痛</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">生韭菜或韭菜根30克，切细，黄酒90毫升，煮沸后，趁热饮服，每日l～2剂。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">23、治金疮出血</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">金疮，中医指刀箭等金属器械造成的伤口。韭汁和风化石灰，日干，每用为末，敷之。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">24、治聤耳出汁</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭汁日滴三次。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">25、治百虫入耳不出</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">捣韭汁，灌耳中。</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<br />\r\n\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#0074C1;font-size:18px;\">26、抗疲劳</span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\"> </span></strong>\r\n		</p>\r\n\r\n		<p style=\"text-align:left;\">\r\n			<strong><span style=\"color:#595959;font-size:18px;\">韭菜中含有蒜氨酸，这种物质可以转变为大蒜素，又与维生素B1结合生成蒜硫胺素，而蒜硫胺素具有与维生素相同的生理活性，能够加速乳酸(疲劳物质)分解，因此它具有抗疲劳的作用。</span></strong>\r\n		</p>\r\n</pre>\r\n		<div style=\"text-align:left;\">\r\n			<span><span style=\"font-size:18px;font-weight:normal;\"><br />\r\n</span></span>\r\n		</div>\r\n</pre>\r\n		<div style=\"text-align:left;font-size:18px;font-weight:normal;\">\r\n			<span><br />\r\n</span>\r\n		</div>\r\n	</h1>\r\n</pre>\r\n	<blockquote style=\"font-family:sans-serif;font-size:medium;color:#333333;\">\r\n<pre></pre>\r\n	</blockquote>\r\n	<p style=\"font-family:sans-serif;font-size:16px;\">\r\n		<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/U1U0mzxhA4R31RxM0Md9V4P1mVqN9x.gif\" width=\"100%\" />\r\n	</p>\r\n	<p style=\"font-family:sans-serif;font-size:16px;\">\r\n		<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/pRc7S97s053rKGsNRrYTZtNN0K9c9y.png\" width=\"100%\" />\r\n	</p>\r\n	<div style=\"font-family:sans-serif;font-size:16px;\">\r\n		<br />\r\n	</div>\r\n<span><span style=\"font-size:16px;\"></span></span>\r\n</p>', '2017-10-04 15:34:22');
INSERT INTO `article_data` VALUES ('55', '<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<img src=\"http://weiqin.xingchen123.top/attachment/images/2/2017/09/BW6mO6K5QZ5NEj8042Q5652V052mq5.gif\" width=\"100%\" class=\"weixin\" />\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;color:#3E3E3E;text-align:center;\">\r\n	<iframe frameborder=\"0\" class=\"we7-iframe\" src=\"http://v.qq.com/iframe/player.html?vid=s0324plks97&amp;tiny=0&amp;auto=0\" width=\"300\" height=\"200\">\r\n	</iframe>\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\">&nbsp; 所以现在你有两个选择：</span></strong></span></strong>\r\n</p>\r\n<h1 style=\"color:#3E3E3E;font-family:Arial;background-color:#FFFFFF;\">\r\n</h1>\r\n<h1 style=\"color:#3E3E3E;font-family:Arial;background-color:#FFFFFF;\">\r\n	<p style=\"text-align:justify;color:#494429;font-family:微软雅黑;font-size:medium;\">\r\n		<span style=\"font-size:18px;\"><strong><span><strong><span style=\"color:#FF0000;\">1. 无视 ,</span></strong></span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:justify;color:#494429;font-family:微软雅黑;font-size:medium;\">\r\n		<span style=\"font-size:18px;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:18px;color:#FF0000;\">2. 让更多人看到！引起重视！</span></strong></span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:justify;color:#494429;font-family:微软雅黑;font-size:medium;\">\r\n		<span style=\"font-size:18px;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:18px;color:#FF0000;\">我选择了让更多人看到，因为我不想冷漠！</span></strong></span></strong></span>\r\n	</p>\r\n</h1>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><img width=\"345\" src=\"http://img.8htq.com.cn/upload/20160922/image36/147451297736.jpg?wxfrom=