INSERT INTO `article_data` VALUES ('66', '<p style=\"font-family:sans-serif;font-size:16px;text-align:center;\">\r\n	<span style=\"color:#888888;font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\">点击上方蓝字</span><span style=\"color:#888888;font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\">「</span><span style=\"font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\"><span style=\"color:#548DD4;\"><strong>人民快报</strong></span></span><span style=\"color:#888888;font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\">」</span><strong><span style=\"color:#548DD4;\"></span></strong><span style=\"color:#888888;font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\">关注</span> \r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;text-align:center;\">\r\n	<span style=\"color:#888888;font-family:微软雅黑;font-size:12px;background-color:#FFFFFF;\"> \r\n	<iframe frameborder=\"0\" class=\"we7-iframe\" src=\"http://v.qq.com/iframe/player.html?vid=n014977ks4m&tiny=0&auto=0\" width=\"300\" height=\"200\">\r\n	</iframe>\r\n</span> \r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:\" background-color:#ffffff;\"=\"\"> <img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/l4e86g4VEi8C6hG6E8vh9Mx3cmH69m.gif\" width=\"100%\" alt=\"images/3/2017/09/l4e86g4VEi8C6hG6E8vh9Mx3cmH69m.gif\" /> \r\n	</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:\" background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">人这一辈子，无非就是个过程，</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">荣华花间露，富贵草上霜，</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">生不带来，死不带去，</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">得意些什么?失意些什么?</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">顺其自然、随遇而安，</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">如行云般自在，像流水般洒脱，</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\">才是人生应有的态度。<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/qVINAkI0IvvoIQr0iRqdr70Ow0yZ78.gif\" width=\"100%\" alt=\"images/3/2017/09/qVINAkI0IvvoIQr0iRqdr70Ow0yZ78.gif\" /></span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/uNF023lx8UqjV8vjnVLl1lJ1Vct3VF.png\" width=\"100%\" alt=\"images/3/2017/09/uNF023lx8UqjV8vjnVLl1lJ1Vct3VF.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><span style=\"color:#FF2941;\">过去，忘掉；现在，拼搏；将来，畅想；活在当下，行在今日。</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/zE39d8gQKjkJK373k9K69uQ36EEJ9Z.png\" width=\"100%\" alt=\"images/3/2017/09/zE39d8gQKjkJK373k9K69uQ36EEJ9Z.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;font-size:20px;\"><strong>人生在世，幸也好，不幸也好，都是过去，只有放下才能轻松前行。</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/Y4XryK4PzZe77ypGe7Lp8ByEz7GGXx.png\" width=\"100%\" alt=\"images/3/2017/09/Y4XryK4PzZe77ypGe7Lp8ByEz7GGXx.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><span style=\"font-size:20px;\">人生如梦不是梦，因为太真实；生活如水不是水，因为有苦涩。</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:inherit;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/G49iGE4HI9i30ke5h0yH4Cuey9RCKi.png\" width=\"100%\" alt=\"images/3/2017/09/G49iGE4HI9i30ke5h0yH4Cuey9RCKi.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><span style=\"font-size:20px;\">背不动的就放下，恨不过的就抚平，珍惜现在拥有的。</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/hv5wXHw5d99x9OS0J9OODXSsO35T55.png\" width=\"100%\" alt=\"images/3/2017/09/hv5wXHw5d99x9OS0J9OODXSsO35T55.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><span style=\"font-size:20px;\">重要的是过好今生。</span></strong></span><span style=\"color:inherit;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:inherit;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/mqttIaiV5UikzqICzauIaCmk2CCKCl.png\" width=\"100%\" alt=\"images/3/2017/09/mqttIaiV5UikzqICzauIaCmk2CCKCl.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><span style=\"font-size:20px;\">不要以为，不要以为，不要自以为。</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/juN7mu2nogE6P27po6pLm858P5onzM.png\" width=\"100%\" alt=\"images/3/2017/09/juN7mu2nogE6P27po6pLm858P5onzM.png\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><span style=\"font-size:20px;\">我们必须：把握今天，珍惜现在，过好今生。</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:inherit;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/J1CBRZlTIGtgEyYJSgaJaaLtLySSFI.png\" width=\"100%\" alt=\"images/3/2017/09/J1CBRZlTIGtgEyYJSgaJaaLtLySSFI.png\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF0000;\"><strong><span style=\"font-size:20px;\">这篇文章太好了！</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF0000;\"><strong><span style=\"font-size:20px;\">人手一份！赶紧给朋友送一份！</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF0000;background-color:#FFFB00;\"><strong><span style=\"font-size:20px;\">愿收到的你一生幸