INSERT INTO `article_data` VALUES ('101', '<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<img src=\"http://weiqin.xingchen123.top/attachment/images/2/2017/09/BW6mO6K5QZ5NEj8042Q5652V052mq5.gif\" width=\"100%\" class=\"weixin\" /><br />\r\n	<iframe class=\"video_iframe\" height=\"270\" frameborder=\"0\" width=\"100%\" src=\"https://v.qq.com/iframe/player.html?vid=e0154eo872n\">\r\n	</iframe>\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;color:#3E3E3E;\">\r\n	<span style=\"font-family:宋体;color:#17365D;\"><strong>说的真对，别私存，让所有人都看看吧！</strong></span>\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<blockquote style=\"color:#002DCF;text-align:justify;background-color:#FFFFF0;\">\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:30px;\"><strong class=\"\">家庭教育方式很重要</strong></span>\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★宠出来的孩子<span style=\"font-size:18px;color:#000000;\">--危险</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★捧出来的孩子<span style=\"font-size:18px;color:#000000;\">--霸道</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★惯出来的孩子<span style=\"color:#000000;font-size:18px;\">--任性</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★娇出来的孩子<span style=\"color:#000000;font-size:18px;\">--脆弱</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★打出来的孩子<span style=\"color:#000000;font-size:18px;\">--逆反</span>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<img width=\"310\" src=\"http://img.8htq.com.cn/upload/20160929/image33/147511670633.jpg?tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" height=\"196\" style=\"width:310px;height:196px;\" />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★骂出来的孩子<span style=\"color:#000000;font-size:18px;\">--糊涂</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★逼出来的孩子<span style=\"color:#000000;font-size:18px;\">--出格</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★磨出来的孩子<span style=\"color:#000000;font-size:18px;\">--坚强</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★苦出来的孩子<span style=\"color:#000000;font-size:18px;\">--懂事</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★教出来的孩子<span style=\"color:#000000;font-size:18px;\">--传统</span>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n★闯出来的孩子<span style=\"font-size:18px;color:#000000;\">--勇敢</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★搏出来的孩子<span style=\"font-size:18px;color:#000000;\">--成功</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★表扬出的孩子<span style=\"color:#000000;font-size:18px;\">--自信</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★溺爱出的孩子<span style=\"font-size:18px;color:#000000;\">--依赖</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★哄出来的孩子<span style=\"color:#000000;font-size:18px;\">--虚伪</span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n★纵容出的孩子<span style=\"font-size:18px;color:#000000;\">--傲慢</span>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<img width=\"308\" src=\"http://img.8htq.com.cn/upload/20160929/image17/147511670717.jpg?tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" height=\"217\" style=\"width:308px;height:217px;\" />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#000000;\"><strong><span style=\"font-size:20px;\">指责中长大的孩子</span></strong></span><span style=\"color:#0070C0;\"><strong><span style=\"font-size:20px;\">，<span style=\"color:#000000;\">将来容易怨天尤人。</span></span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">敌意中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易好斗逞强。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">恐惧中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易畏首畏尾。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">怜悯中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易自怨自艾。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">嘲讽中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易消极退缩。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">嫉妒中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易钩心斗角。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">羞辱中长大的孩子<span style=\"color:#0070C0;\">，</span>将来容易心怀内疚。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">容忍中长大的孩子<span style=\"color:#0070C0;\">，</span>将来必能极富耐性。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#0070C0;\"><strong><span style=\"color:#000000;font-size:20px;\">孩子是父母的镜子，父母是孩子的榜样。</span></strong></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"font-size:20px;color:#FFFFFF;font-weight:bold;text-align:center;\">\r\n		说的太对了！\r\n	</p>\r\n	<p style=\"font-size:20px;color:#FFFFFF;font-weight:bold;text-align:center;\">\r\n		传出去，不要私存！\r\n	</p>\r\n</blockquote>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<img width=\"384\" src=\"http://img.8htq.com.cn/upload/20161015/image80/1