INSERT INTO `article_data` VALUES ('120', '<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<img src=\"http://weiqin.xingchen123.top/attachment/images/2/2017/09/BW6mO6K5QZ5NEj8042Q5652V052mq5.gif\" width=\"100%\" class=\"weixin\" />\r\n</p>\r\n<h2 class=\"\" style=\"font-weight:400;font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	诗歌：只愿我的生命更加像你（好听的诗歌）\r\n</h2>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<iframe class=\"video_iframe\" height=\"270\" frameborder=\"0\" width=\"100%\" src=\"https://v.qq.com/iframe/player.html?vid=b05449z54jx\">\r\n	</iframe>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">一天24个小时，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">愁也一天，乐也一天，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">何不开开心心过好每一天。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">一年365天，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">好也得过，赖也得过，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">何不舒舒坦坦过好每一年。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">一辈子不过三万天，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">穷也得活，富也得活，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">怎么活都是活，活就好好得活!</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\"><img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/pepT707J07pm0TvMI5A6FZP5fUTFzJ.png\" width=\"307\" alt=\"images/3/2017/09/pepT707J07pm0TvMI5A6FZP5fUTFzJ.png\" height=\"207\" style=\"width:307px;height:207px;\" /></span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">你活得疲惫吗，怨谁？怨自己。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">为了生活你日夜奔波，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">省吃俭用钱也没攒几个，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">最后烙下个病身子，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">给座金山也没福享用了；</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">你活得纠结吗，怨谁？怨自己。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">想不开的事你使劲琢磨，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">明明知道没结果还陷入漩涡，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">想不开是劫想开了也是结，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">太折磨自己何苦呢。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\"><img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/v21dROS2y1VH1DivP4ZILpsS9HPYaV.png\" width=\"305\" alt=\"images/3/2017/09/v21dROS2y1VH1DivP4ZILpsS9HPYaV.png\" height=\"212\" style=\"width:305px;height:212px;\" /></span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">你活得拘谨吗，怨谁？怨自己。</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">想说的话不敢说，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">想爱的人止于羞涩，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">总是活给别人看，</span></strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#3E3E3E;font-family:微软雅黑;text-align:center;\">\r\n	<strong><span style=\"color:#974806;font-size:20px;\">丢掉了多少独一无二的