INSERT INTO `article_data` VALUES ('200', '<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	 <img width=\"268\" src=\"http://img.mp.itc.cn/upload/20160926/95d070de2ac34595bdf84eabe2b32ab2_th.jpeg\" height=\"150\" style=\"width:268px;height:150px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\"><strong>不管是老公，情人，还是追你的男人</strong></span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"280\" src=\"http://img.mp.itc.cn/upload/20160926/aeedb9634139412c8eb1e26e0f1eb40b_th.jpeg\" height=\"190\" style=\"width:280px;height:190px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><span style=\"font-size:16px;\">在情人节没有送你礼物，马上可以换掉了。</span></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"290\" src=\"http://img.mp.itc.cn/upload/20160926/c1534a0bf0e346ccbfa2cca42c0183ad_th.jpeg\" height=\"240\" style=\"width:290px;height:240px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><span style=\"font-size:16px;\">原因一：他不知道情人节，他是白痴。</span></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"320\" src=\"http://img.mp.itc.cn/upload/20160926/ddea01b9055540f8ad0b3ea3d3b0f253_th.jpeg\" height=\"230\" style=\"width:320px;height:230px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><span style=\"font-size:16px;\">原因二：他知道情人节，不送礼物，他是铁公鸡！</span></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><img width=\"305\" src=\"http://img.mp.itc.cn/upload/20160926/ba1171e4e7ee49b3b780ad096318e4b7_th.jpeg\" height=\"205\" style=\"width:305px;height:205px;\" /></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><span style=\"font-size:16px;\">原因三：情人节不送礼物，什么时候送？</span></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong><img width=\"300\" src=\"http://img.mp.itc.cn/upload/20160926/dbce827366a54972aabfcb763cda73eb_th.jpeg\" height=\"249\" style=\"width:300px;height:249px;\" /></strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <strong>原因四：情人节不送给你，他想送给谁？</strong>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">关于爱情，马伊琍老师说：“真正的爱情一定是双方的，它计较的不是我付出多少得到多少，而是我对自己妥协了多少。”</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"285\" src=\"http://img.mp.itc.cn/upload/20160926/9512776042954ba9ad71a7ce025bbf40_th.jpeg\" height=\"215\" style=\"width:285px;height:215px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">李晨说会一直保护张馨予</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"285\" src=\"http://img.mp.itc.cn/upload/20160926/d2afba5837084a9f92f69c57164c1c1a_th.jpeg\" height=\"176\" style=\"width:285px;height:176px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">张翰说郑爽是她最爱的女人</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">▼</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"265\" src=\"http://img.mp.itc.cn/upload/20160926/15c96c2e82b64e0bbbdeb197b958d80b_th.jpeg\" height=\"160\" style=\"width:265px;height:160px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">谢霆锋说不介意张柏芝的过去</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">▼</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"250\" src=\"http://img.mp.itc.cn/upload/20160926/62aef0c47a054ce4a7fa603d29be2b24_th.jpeg\" height=\"190\" style=\"width:250px;height:190px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">文章说这辈子最骄傲的是他女人叫马伊琍</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">▼</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"auto\" src=\"http://img.mp.itc.cn/upload/20160926/d1e176d3c40048c18eee23b36342fc58.jpeg\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">陈赫说娶到许婧是他这辈子最大的幸福</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">▼</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"290\" src=\"http://img.mp.itc.cn/upload/20160926/da53beaf60284ce7b438270f7dc5e4be_th.jpeg\" height=\"198\" style=\"width:290px;height:198px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">可当其他女人出现后才发现，</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">之前说的再好都没有什么用，</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">所以男人的话有时候真的当个玩笑就好了。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"color:#0070C0;font-size:14px;\">女人要什么？在物质和一个傻傻对她好的男人面前，大部分女人 还是会选择对她好的男人。别说她走了是因为你没钱，当初是她在你没钱的时候选择了你。也许更多的时候多问问自己，你有没有给她足够的踏实感和安全感？</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">很多时候，女人的离开都是因为没有足够的安全感，让她觉得不踏实，看不到希望！任何女人都有一颗想被保护的心，女强人不过是隐藏了脆弱的一面而已，请你好好呵护身边爱你的女人，不要让她独立到让人心疼。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">再强势的女人遇到喜欢的男人也会柔情似水。如果可以依靠，没有哪个女人愿意做女汉子。所以不要说女人强势，只是你驾驭不了而已。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"320\" src=\"http://img.mp.itc.cn/upload/20160926/5a818f7ed0fa4d738d57dcce90427e3f_th.jpeg\" height=\"239\" style=\"width:320px;height:239px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">很多男人都说女人现实，爱慕虚荣，情愿坐在宝马上哭，也不坐在自行车上笑，其实好女人不是这样的，如果你真的对她好，真的爱她，她会愿意陪你白手起家经历风雨！很多时候，她离开的原因是———你骑一个烂自行车，还天天让她哭……</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">马伊琍老师说：</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"color:#0070C0;font-size:14px;\">等我女儿长大了，我会告诉她！如果一个男人心疼你挤公交，埋怨你不按时吃饭，一直提醒你少喝酒伤身体，阴雨天嘱咐你下班回家注意安全，生病时发搞笑短信哄你...请不要理他！然后跟那个可以开车送你、生病陪你、吃饭带你、下班接你、跟你说 「什么破工作别干了！跟我回家！」的人在一起……</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <img width=\"270\" src=\"http://img.mp.itc.cn/upload/20160926/771b958471134a2786c86760550ebca1_th.jpeg\" height=\"219\" style=\"width:270px;height:219px;\" />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">如果一个人说喜欢你</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">请等到他对你百般照顾时再相信。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">如果他答应带你去的地方</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">等他订好机票再开心。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">如果他说要娶你</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">等他买好戒指跪在你面前再感动。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">感情不是说说而已</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:14px;\">我们已经过了耳听爱情的年纪。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"color:#0070C0;font-size:14px;\">现在的男人啊、都希望女人说话温柔点儿，对你体贴点儿，长得还要漂亮点儿，身材好一点儿，经济独立点儿，持家又得贤惠点儿，对你爹妈孝顺点儿，对兄弟朋友客气点儿，还得勤快点，床上还要主动点。</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"color:#0070C0;font-size:14px;\">我要问了，你要求这么多，是因为你的哪一点儿？你是高一点儿？帅一点儿？还是你银行卡里有七八个小数点儿？又或者是温柔稳重会心疼人点儿？！ 什么都没有，你就给姐老实一点儿！</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	  <span style=\"font-size:16px;\">要么给我爱，要么给我钱，要么给我滚！</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong><span style=\"background-color:#548DD4;font-size:20px;\"><strong><span style=\"font-size:18px;\">想看更多内部视频请添加小雨微信号：<span style=\"background-color:#000000;color:#FFFF00;font-size:24px;\">chengdu987</span></span></strong></span></strong></span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<img title=\"147495814661.jpg\" src=\"http://img.8htq.com.cn/upload/20160927/image61/147495814661.jpg\" width=\"465\" height=\"86\" style=\"width:465px;height:86px;\" />\r\n</p>\r\n<p style=\"font-family:sans-serif;font-size:16px;\">\r\n	<img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/EL1PlHFfak77h3PaP4pPlZhJ81EfFl.png\" width=\"100%\" /><img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/fVSDvVgLZm8VvVG3vVlSOG21G82Al2.gif\" width=\"100%\" alt=\"姐妹帮.gif\" />\r\n</p>', '2017-10-07 12:03:36');
