INSERT INTO `article_data` VALUES ('230', '<p>\r\n	<img src=\"/static/upload/article/20171007170156_622.gif\" title=\"20171007170156_622.gif\" alt=\"20171007170156_622.gif\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">下面有6扇门，请选择其中最吸引你的一个。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><img src=\"/static/upload/article/20171007170234_563.png\" title=\"20171007170234_563.png\" alt=\"20171007170234_563.png\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\"></span><span style=\"color:#000000;\">向</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">上</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">测</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">试</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">结</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">果</span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\">↓↓↓</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#000000;\"><br />\r\n</span></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第一扇门</span></span></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><img src=\"/static/upload/article/20171007170447_125.png\" title=\"20171007170447_125.png\" alt=\"20171007170447_125.png\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你喜欢安静，渴望灵魂的自由，喜欢自己一人呆着，任思绪放飞。</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">非常独立，不喜欢依赖他人，尽管有时候不免逞强。</span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你追求的幸福很简单，陪伴是最长情的告白。</span><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span></span></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"><span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第二扇门</span><br />\r\n</span></span></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FF4C41;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#E53333;\"><span style=\"color:#000000;background-color:#E53333;\"> </span></span> \r\n</p>\r\n<pre></pre>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170645_420.png\" title=\"20171007170645_420.png\" alt=\"20171007170645_420.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你很聪明，创造性强，以独一无二的方式看待这个世界。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你内心敏感脆弱，渴望简单的生活，享受独处的时光。但真正懂你的那个人总会出现。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">朋友不需要太多，有你一个就足够。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第三扇门</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170727_893.png\" title=\"20171007170727_893.png\" alt=\"20171007170727_893.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你性格外向，充满活力，喜欢四处结交朋友，待人真诚友善。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你热爱大自然，勇于尝试接受新鲜的事物，享受着生活中的每一个小惊喜。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">这样的你，值得拥有多彩的人生。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第四扇门</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170805_436.png\" title=\"20171007170805_436.png\" alt=\"20171007170805_436.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你喜欢冒险。旅程中所有不可预知的事物，都让你兴奋不已。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">敢于突破常规，打破偏见，走一条与众不同的路径。你随和好相处，家人朋友喜欢寻求你的建议。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">勇敢无畏的你，广阔的世界都是你的舞台。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第五扇门</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170837_234.png\" title=\"20171007170837_234.png\" alt=\"20171007170837_234.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你渴望安然恬静的生活，随遇而安。不喜欢吵闹的人群，却深爱陪在身边的人们。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">温柔细心，擅长聆听，富有同情心，重感情，总是为爱默默付出。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">被你爱上的人，是幸福的。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#FFFFFF;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#AB1942;\">第六扇门</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170908_346.png\" title=\"20171007170908_346.png\" alt=\"20171007170908_346.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-size:15px;background-color:#FFFFFF;font-family:微软雅黑, sans-serif;line-height:25px;\">你处事冷静沉着，内敛稳重。</span><span style=\"color:#3E3E3E;font-size:15px;background-color:#FFFFFF;font-family:微软雅黑, sans-serif;line-height:25px;\">喜欢思考，擅长从生活小事中悟出人生大道理。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">你走的每一步都是理性的、深思熟虑的。</span><span style=\"color:#3E3E3E;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">擅长处理难题，许多人都会寻求你的建议，你也乐于帮助他们。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#C00000;font-family:微软雅黑, sans-serif;font-size:16px;background-color:#FFFFFF;\">这样的你，值得朋友家人的信赖。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170940_954.png\" title=\"20171007170940_954.png\" alt=\"20171007170940_954.png\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007170948_326.gif\" title=\"20171007170948_326.gif\" alt=\"20171007170948_326.gif\" />\r\n</p>', '2017-10-07 17:09:53');
