INSERT INTO `article_data` VALUES ('233', '<p>\r\n	<img src=\"http://weiqin.xingchen123.top/attachment/images/2/2017/09/BW6mO6K5QZ5NEj8042Q5652V052mq5.gif\" width=\"100%\" class=\"weixin\" />\r\n</p>\r\n<p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">人生在世都是戏。</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">就连微信分组也入戏三分，</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">那天我不小心瞄了一眼老板的微信分组，</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">差点惊呆了……</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<img src=\"/static/upload/article/20171007174722_549.png\" title=\"20171007174722_549.png\" alt=\"20171007174722_549.png\" />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\"><br />\r\n</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">发现不加班的里面竟然有我的名字！Σ( ° △ °|||)︴</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">吓得我赶紧把刚收拾好的书包放下了……</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">随后，我又看到了不同岗位同事那五花八门的分组标签</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:微软雅黑;font-size:15px;color:#000000;\">这个分组的世界真是很精彩呢！<br />\r\n</span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:15px;color:#000000;\"><strong><span style=\"font-family:微软雅黑;color:#FF6827;\">开发的同事是这样标记分组的……</span></strong></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<img src=\"/static/upload/article/20171007174730_851.png\" title=\"20171007174730_851.png\" alt=\"20171007174730_851.png\" />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<span style=\"font-size:15px;color:#000000;\"><strong><span style=\"font-family:微软雅黑;color:#FF6827;\">产品经理如实说……</span></strong></span>\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<img src=\"/static/upload/article/20171007174738_250.png\" title=\"20171007174738_250.png\" alt=\"20171007174738_250.png\" />\r\n	</p>\r\n	<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#3E