INSERT INTO `article_data` VALUES ('234', '<p>\r\n	<img src=\"/static/upload/article/20171007180124_383.gif\" title=\"20171007180124_383.gif\" alt=\"20171007180124_383.gif\" />\r\n</p>\r\n<p>\r\n	<iframe class=\"video_iframe\" height=\"270\" frameborder=\"0\" width=\"100%\" src=\"https://v.qq.com/iframe/player.html?vid=u0372hv2kz1\">\r\n	</iframe>\r\n</p>\r\n<p>\r\n	<img src=\"/static/upload/article/20171007180136_588.png\" title=\"20171007180136_588.png\" alt=\"20171007180136_588.png\" />\r\n</p>\r\n<p>\r\n	<img src=\"/static/upload/article/20171007180146_480.gif\" title=\"20171007180146_480.gif\" alt=\"20171007180146_480.gif\" />\r\n</p>', '2017-10-07 18:01:51');
INSERT INTO `article_data` VALUES ('235', '<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"><img src=\"http://weiqin.xingchen123.top/attachment/images/2/2017/09/BW6mO6K5QZ5NEj8042Q5652V052mq5.gif\" width=\"100%\" class=\"weixin\" /><br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">长假前夕，一位全职妈妈哭诉</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-family:微软雅黑;color:#FF4C41;\">国庆想去外地旅游</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-family:微软雅黑;color:#FF4C41;\">却被家婆和老公骂自私</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">字里行间透露着心酸</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">到底是怎么回事？</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"></span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"> <img src=\"/static/upload/article/20171007180956_239.png\" title=\"20171007180956_239.png\" alt=\"20171007180956_239.png\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-size:14px;color:#888888;\">△网络图（图文无关）</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-size:14px;color:#888888;\"></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">以下是原帖全文</span> \r\n	</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">▼<br />\r\n</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#595959;font-family:微软雅黑;\">网友@二捣蛋：</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">这两天和家人闹翻了，我想趁着国庆长假去外地旅游，可老公、婆婆强烈反对，现在家里因为这个事情弄得鸡飞狗跳，<span style=\"color:#FF4C41;\">他们都指责我自私，不负责任，不配当妈，我觉得十分憋屈。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">说说事情的经过，我有两个孩子，一个５岁，一个２岁，两个都是我一手拉扯大的，<span style=\"color:#FF4C41;\"><span>虽然公公婆婆也在本地，但不曾帮过我半分</span>，</span>连孩子生病都是我一个人拉着两个孩子上医院的。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">和老公结婚都有六七年了，结婚后我就辞职专心在家当起了全职妈妈，<span style=\"color:#FF4C41;\"><span>没有请保姆</span>，</span>是一个人拉大两个孩子的，<span style=\"color:#FF4C41;\">老公工作很忙，也没能帮上什么忙。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">今年放长假，我老公也难得放几天假，所以我就想趁着这个机会出去走一走，<span style=\"color:#FF4C41;\"><span>让婆婆帮忙照顾一下孩子</span>。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">前几天一起吃饭的时候，向他们提出想外出走一走，结果我没说完就被婆婆呛了一句：<span style=\"color:#FF4C41;\">“都当妈了心还收不住！”</span></span><span style=\"color:#595959;font-family:微软雅黑;\">我老公也是<span style=\"color:#FF4C41;\">强烈反对</span>我外出。我公公没说什么，没赞成也没反对。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">我老公和我婆婆在饭桌上轮番说我，<span style=\"color:#FF4C41;\"><span>自私啦，不负责啦，心野啦</span>，</span>我忍不住和他们吵了起来。 <br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/static/upload/article/20171007181026_159.png\" title=\"20171007181026_159.png\" alt=\"20171007181026_159.png\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">现在家里闹僵了，我和我老公几天没说话，婆婆那边也没再去过。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#FF4C41;\">真的是我自私吗？</span><span style=\"color:#595959;font-family:微软雅黑;\">两个孩子都大了啊，小的也不吃母乳了，两个的关系也很好，很少打架。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\">唉，我现在也不知怎么是好，不管他们反对，一个人出门？还是苦哈哈地在家里带娃算了。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"></span><span style=\"color:#595959;font-family:微软雅黑;\">面对这位全职妈妈的遭遇</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">很多人表示：<span style=\"color:#D92142;\"><strong><span style=\"font-size:20px;\">感同身受</span></strong></span></span> \r\n</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">▼</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\"></span></strong> \r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@pokkalin ：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">最不喜欢就是那句一来就说“<span>都当妈了心还收不住！</span>”，<span style=\"color:#FF4C41;\">当妈就只能在家当保姆吗？</span><span style=\"color:#FF4F79;\"><br />\r\n</span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"color:#595959;font-family:微软雅黑;\"><span style=\"color:#FF4F79;\"><br />\r\n</span></span> \r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@yaya351：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">又不是去两个月，再说小的又不是刚出生，凭什么说很小不让出门？整个国庆也就去八天，都不行么？也是醉了。<span style=\"color:#FF4C41;\">如果女人结婚生子后连这点自由都得不到支持，还有什么意思？</span></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@ypsivy ：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">带小孩真是很累很困身的，抱抱楼主！不过，你婆婆就算了，毕竟帮你带是人情，不帮你带是道理，但<span style=\"color:#FF4C41;\">你老公真不是个东西，不体谅你，还跟着他妈妈一起指责你。</span></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@扬bb107：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">出去几天，一点都不过分，又不是从此抛下小孩不管，有必要说到人格那里去吗，自私，不配做妈妈，<span style=\"color:#FF4C41;\">就是因为有这些思想，才会有人产后得抑郁症，宽容点吧</span></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#595959;font-family:微软雅黑;\"> <br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">但也有人责怪楼主：</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">这样的性格<span style=\"color:#D92142;\"><strong><span style=\"font-size:20px;\">太懦弱</span></strong></span></span> \r\n	</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">▼</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#595959;font-family:微软雅黑;\"></span></strong><strong><span style=\"color:#595959;font-family:微软雅黑;\">@chachacha1：</span></strong><span style=\"font-family:微软雅黑;color:#FF4C41;\">我觉得你真是混得连个保姆都不如了。</span><span style=\"color:#595959;font-family:微软雅黑;\">如果你是请来的保姆，干了六七年了，他们还真不敢这么给你脸色</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<strong> <span style=\"color:#595959;font-family:微软雅黑;\">@ireneshine：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">请问你这样的老公还留着过年？就只负责给你家用吗？</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#595959;font-family:微软雅黑;\">@幸福长毛兔：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">这事情得自己想办法解决，<span style=\"color:#FF4C41;\">这么大人了不会连这点主见都没有吧？</span>我是不觉得女人当了妈，就要放弃一切其他的东西。但是<span style=\"color:#FF4C41;\">就你婆婆和你老公这样的态度，你以后也很难有自己的时间和自由。</span></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"></span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"> <img src=\"/static/upload/article/20171007181056_728.png\" title=\"20171007181056_728.png\" alt=\"20171007181056_728.png\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-size:14px;color:#888888;\">△网络图（图文无关）</span><span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">也有网友认为</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">这件事必须<span style=\"color:#D92142;\"><strong><span style=\"font-size:20px;\">以孩子为重</span></strong></span></span> \r\n</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"><strong><span style=\"color:#FF4F79;font-size:20px;\"><span style=\"color:#595959;\">▼</span></span></strong></span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@汤汁太太：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">小的才两岁，放放吧，起码到上幼儿园才去</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@carrieric：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">如果婆婆一直没带过，那么把两个孩子交给他们我自己都不放心，可以跟老公商量把两个都带出去，<span style=\"color:#FF4C41;\">一家四口一起，老公也能体会到一起带孩子的不容易。</span></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@幸福蛋炒饭：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">是我做不到。就因为全职带大的更放不下。说和老公一起出去还觉得没什么，<span style=\"color:#FF4C41;\">放下孩子自己去玩真的玩得开心吗？</span></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">还有人明确站队</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\">支持婆婆</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"font-size:20px;color:#D92142;\"><strong><span style=\"font-family:微软雅黑;\">长辈没有义务带孩子</span></strong></span> \r\n</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;background-color:#ffffff;text-align:center;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"></span><span style=\"color:#595959;font-family:微软雅黑;\"><strong><span style=\"color:#FF4F79;font-size:20px;\"><span style=\"color:#595959;\">▼</span></span></strong></span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@星辰_9583：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">把两小孩扔给老人家去旅游，<span style=\"color:#FF4C41;\"><span>老人好像也没这义务帮你带吧</span>，</span>更何况一直是妈妈带大的，你以为老人很好带呀？那么理直气壮，有骨气就两个都带出去让小孩见下外面的世界。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\">@ireneshine：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">丢下俩娃给老人带，自己去旅游吗？我也是1个5岁，1个1岁半，<span style=\"color:#FF4C41;\"><span>这种要求我说不出口</span>，</span>最多辛苦点，一家四口一起出游。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <img src=\"/static/upload/article/20171007181112_555.png\" title=\"20171007181112_555.png\" alt=\"20171007181112_555.png\" /><span style=\"font-size:14px;color:#888888;\">△网络图（图文无关）</span><span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <strong><span style=\"color:#007AAA;font-size:18px;\"></span></strong> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#D92142;\"><strong><span style=\"font-size:18px;\">“问题还是出在小夫妻身上”</span></strong></span><strong><span style=\"color:#007AAA;font-size:18px;\"><br />\r\n</span></strong> \r\n	</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"><strong><span style=\"color:#FF4F79;font-size:20px;\"><span style=\"color:#595959;\">▼</span></span></strong></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p>\r\n	<span><strong><span style=\"color:#595959;font-family:微软雅黑;\">@卷卷斗斗猪：</span></strong><span style=\"color:#595959;font-family:微软雅黑;\">一个称职体贴的丈夫，至少应该让全职妈妈每个星期放假一天（是彻底的休息）。老婆带着两个小孩无论从精神到身体都相当疲劳，显然你丈夫没做到这一点。<span style=\"color:#FF4C41;\">我是宝爸，我一直这样执行这个理念。</span></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span><strong><span style=\"font-size:15px;color:#595959;font-family:微软雅黑;\">@瓦拉：</span></strong><span style=\"font-family:微软雅黑;color:#FF4C41;\">自己的娃自己带，这个才是最正的三观，把责任推给别人本身就缺乏理据。</span><span style=\"color:#595959;font-family:微软雅黑;\"><span style=\"color:#FF4F79;\"></span>但是如果家人愿意帮忙，那也无所谓，出去不过十天八天而已。你的问题在于，你老公都不认同你，这才是关键。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#595959;font-family:微软雅黑;\"><strong>@iChris：</strong>只能说，全职妈妈不好当，他们觉得你没收入，还花钱出去旅游，<span style=\"color:#FF4C41;\"><span>如果你有稳定的高收入，看看还会不会这样直接喷你</span>，</span>至少会客气点委婉点。</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\">\r\n	对此，楼主再次跟帖回应网友<span style=\"color:#007AAA;font-size:18px;\"></span>：<br />\r\n<span style=\"font-size:18px;color:#D92142;\"><strong>这些年太累了</strong></span> \r\n</p>\r\n<p style=\"text-align: center; color: rgb(62, 62, 62);\" font-size:16px;text-align:center;background-color:#ffffff;\"=\"\"> <span style=\"color:#595959;font-family:微软雅黑;\"><span><strong><span style=\"color:#FF4F79;font-size:20px;\"><span style=\"color:#595959;\">▼</span></span></strong></span><br />\r\n</span> \r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"color:#595959;font-family:微软雅黑;\"></span></strong>结婚几年，我都没外出旅游过。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#FF4C41;\">大家都是喷我的多。</span>但我其实真的打算去几天而已，孩子还小，很懂事，也没有反对我出游这件事。<br />\r\n<br />\r\n<span style=\"color:#FF4C41;\"><span>这些年太累了</span>。</span>去年小孩子生病，5月份做了手术，我送完大的去上课，又要回来照顾小的。孩子刚手术完我公公婆婆就出国旅游了，什么忙都不帮。说我婆婆照顾不了两个孩子是不存在的，她退休前是幼儿园园长，有很多年幼教经验。<span style=\"color:#FF4C41;\">不是带不了，是不愿意帮忙而已！！</span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		这几年我都快抑郁了。<span style=\"color:#FF4C41;\">我老公除了工作，什么都不会，他连做饭都不会。</span><br />\r\n<span style=\"font-family:微软雅黑;color:#FF4F79;\"></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"> <img src=\"/static/upload/article/20171007181038_957.png\" title=\"20171007181038_957.png\" alt=\"20171007181038_957.png\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"color:#3E3E3E;font-family:\" font-size:16px;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<h1 style=\"color:#3E3E3E;font-family:Arial;background-color:#FFFFFF;\">\r\n	<p style=\"color:#494429;font-family:微软雅黑;font-size:medium;\">\r\n		<span style=\"font-size:18px;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:18px;color:#FF0000;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><strong><strong><strong><strong><span style=\"color:#D82821;\"><strong><strong><strong><strong><span style=\"color:#7F7F7F;line-height:22.39px;font-size:14px;\"><strong><strong><span style=\"color:#00B0F0;font-size:18px;\"><strong><strong><strong><span style=\"color:#C00000;font-weight:normal;\"><strong><strong><strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><strong><strong><strong><strong><span style=\"color:#D82821;\"><strong><strong><strong><strong><span style=\"color:#7F7F7F;line-height:22.39px;font-size:14px;\"><strong><strong><span style=\"color:#00B0F0;font-size:18px;\"><strong><strong><strong><span style=\"color:#C00000;font-weight:normal;\"><strong><strong><img width=\"319\" src=\"http://img.8htq.com.cn/upload/20160922/image33/147451297733.jpg?wxfrom=5&wx_lazy=1\" height=\"68\" style=\"width:319px;height:68px;\" /><br />\r\n</strong></strong></span></strong></strong></strong></span></strong></strong></span></strong></strong></strong></strong></span></strong></strong></strong></strong></span></strong></span></strong></strong></strong></span></strong></strong></strong></span></strong></strong></span></strong></strong></strong></strong></span></strong></strong></strong></strong></span></strong></span></strong></span></strong></span></strong></span> \r\n	</p>\r\n</h1>\r\n<h2 class=\"\" style=\"font-size:20px;font-weight:400;color:#3E3E3E;text-align:center;font-family:宋体;\">\r\n	<br />\r\n</h2>\r\n<h2 class=\"\" style=\"font-size:20px;font-weight:400;color:#3E3E3E;text-align:center;font-family:宋体;\">\r\n	<strong><span style=\"color:#FF0000;font-size:20px;\"><span style=\"line-height:25.6px;font-family:微软雅黑;color:#7B0C00;\">舆论压力，平台常被封，如需查看更多“</span><span style=\"font-family:微软雅黑;line-height:28.44px;color:#021EAA;\">爆料贪官污吏，欺行霸市，电视不敢曝光的丑闻；南海实时焦点，政治风云，更有正能量弘扬</span><span style=\"line-height:25.6px;font-family:微软雅黑;color:#7B0C00;\">\"的内幕消息请<span style=\"line-height:25.6px;color:#FFFB00;background-color:#000000;\">长按下方二维码</span>识别关注！！！</span></span></strong> \r\n</h2>\r\n<p style=\"font-size:medium;text-align:center;color:#494429;font-family:微软雅黑;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:18px;color:#FF0000;\"><strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><strong><strong><strong><strong><span style=\"color:#D82821;\"><strong><strong><strong><strong><span style=\"color:#7F7F7F;line-height:22.39px;font-size:14px;\"><strong><strong><span style=\"color:#00B0F0;font-size:18px;\"><strong><strong><strong><span style=\"color:#C00000;font-weight:normal;\"><strong><strong><strong><span style=\"font-size:20px;\"><strong><span style=\"color:#FF0000;\"><strong><strong><strong><strong><span style=\"color:#D82821;\"><strong><strong><strong><strong><span style=\"color:#7F7F7F;line-height:22.39px;font-size:14px;\"><strong><strong><span style=\"color:#00B0F0;font-size:18px;\"><strong><strong><strong><span style=\"color:#C00000;font-weight:normal;\"><strong><strong></strong></strong></span></strong></strong></strong></span></strong></strong></span></strong></strong></strong></strong></span></strong></strong></strong></strong></span></strong></span></strong></strong></strong></span></strong></strong></strong></span></strong></strong></span></strong></strong></strong></strong></span></strong></strong></strong></strong></span></strong></span></strong></span></strong></span></strong></span><img src=\"http://my6.mogudao.cn/attachment/images/3/2017/09/fVSDvVgLZm8VvVG3vVlSOG21G82Al2.gif\" width=\"100%\" alt=\"姐妹帮.gif\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '2017-10-07 18:23:42');
INSERT INTO `article_data` VALUES ('237', '<p>\r\n	<img src=\"/static/upload/article/20171007182949_379.gif\" title=\"20171007182949_379.gif\" alt=\"20171007182949_379.gif\" />\r\n</p>\r\n<p>\r\n	<img src=\"/static/upload/article/20171007183005_527.png\" title=\"20171007183005_527.png\" alt=\"20171007183005_527.png\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:24px;\"><strong>人</strong></span>和人的相处，是一门学问，要做好一点，真的不容易。离得太远了，关系就淡了；可靠得太近了，恩恩怨怨就来了。\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-size:16px;font-family:&quot;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	不管是家人朋友，还是同学同事，好起来时可以两人同穿一条裤子，闹翻后可以老死不相往来。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>人生如尺，要有度。感情如面，别越界。人和人相处，一定要把握度，关系再好，也不要走得太近，否则最后也只能渐行渐远了。</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	希望所有朋友，都能牢牢记住，否则受伤的就是你自己！\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:24px;\">0</span></strong><span class=\"\" style=\"font-size:24px;\"><strong>1</strong></span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:center;background-color:#FFFFFF;\">\r\n	<strong>跟谁走得太近，都会是种伤害</strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	朋友之间，太近扎人。言语上不注意，金钱上没分寸，行动里不知尊重，时间久了，谁都会远离。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	亲人之间，天天面对面，打听下你的家里事，分享点小道消息，久而久之，烦了，厌了，远了。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	家人之间，孩子的小家庭你非要介入，另一半的信息天天查看，每天粘着他们没了自我，最终感情却成了隔阂。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>所以，要想和家人、亲朋好友之间和睦相处，就要保持一定的距离。</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	那在生活中，具体如何去保持合适的距离呢？这里有五点建议，供所有朋友参考！\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<img src=\"/static/upload/article/20171007183128_834.png\" title=\"20171007183128_834.png\" alt=\"20171007183128_834.png\" />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:24px;\">0</span></strong><span class=\"\" style=\"font-size:24px;\"><strong>2</strong></span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:center;background-color:#FFFFFF;\">\r\n	<strong>保持一定距离，生活更愉快</strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>1、和伴侣之间，给彼此留一点空间</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	夫妻相处之道，也就是把握夫妻间距离之道。夫妻之间，关系很亲密，但这并不意味着两者之间就不能有些小秘密。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	特别是在兴趣爱好上有差别的夫妻，不能强求对方要和自己有一样的爱好。要保持一定的距离，认同对方的喜好，然后有各自的朋友圈子，和睦愉快相处。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>2、和子女之间，保持\"一碗汤\"的距离</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	随着社会的发展，越来越多的父母能够接受与子女间有一定的距离。但距离不太远，双方都能互相照顾，还可避免一些矛盾和麻烦。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	有界限、有距离、有联系、有守望，最佳的距离就是“一碗汤的距离”。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	生活上，和子女保持“一碗汤”的距离，能常去看望他们，给他们送去一碗汤；心理上，也要和子女保持“一碗汤”的距离，不会因为太热而烫到他们，也不会因为太冷而凉了心意。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>3、和亲人之间，不能没有恭敬心</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	亲情，是难以割舍的情感。和亲人相处，不要太随意，一定要有恭敬心。获得亲人的帮助要感谢，亲人有难处也要及时提供帮助。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	亲人家中的事情，人家愿意说的就好好听着，不愿意说的就少打听，不要干涉，更加不要肆无忌惮。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	每个人心中都有不愿被别人涉足的角落，尊重他就是尊重你们的亲情。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<img src=\"/static/upload/article/20171007183137_734.png\" title=\"20171007183137_734.png\" alt=\"20171007183137_734.png\" />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>4、和朋友之间，时刻牢记无所求</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	有些人和朋友关系亲近一点，就开始有各种要求，如果朋友不满足，就抱怨。这样的朋友相处起来，让人觉得心累。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	真正的友情是纯粹的，不带功利的。朋友帮你是情分，不帮你是本分，不能用道德去绑架他们，要他们不限量地满足你的需求。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	很多朋友关系越搞越糟糕，是因为向对方索求的太多了。这就是造成翻脸的原因。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<span style=\"color:#C00000;\"><strong>5、和陌生人之间，见人宜说三分话</strong></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	君子之交，淡如水。有些人是第一次见，有些人一年见一两次，和这些不熟悉的人相处，就算第一印象再好，也不能没有分寸。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	不要试图窥探别人的隐私，说话留有余地，不能强人所难，说话不能不看时机。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	年龄越大越发现，爱情、亲情、友情，情情珍贵，所以和任何人都不要走得太近，给自己留一点缓和的余地，给别人一些自己的私人空间，这也许就是最美的距离了。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	不必靠太近，各自都有各自的生活，不必离太远，我们的生活圈还有交际。\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<strong>距离产生美，其实就是，彼此尊重，彼此珍惜。</strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<strong><img src=\"/static/upload/article/20171007183148_800.png\" title=\"20171007183148_800.png\" alt=\"20171007183148_800.png\" /><br />\r\n</strong>\r\n</p>\r\n<p style=\"font-size:16px;color:#595959;font-family:微软雅黑;text-align:justify;background-color:#FFFFFF;\">\r\n	<strong><img src=\"/static/upload/article/20171007183156_776.gif\" title=\"20171007183156_776.gif\" alt=\"20171007183156_776.gif\" /><br />\r\n</strong>\r\n</p>', '2017-10-07 18:32:16');
